// A program to determine the sum of the digits of 2**1000
// Javascript is apparently not precise enough (in the Chrome inerpreter anyway) to
// calculate 2**1000 accurately so I've taken wolfram's calculation and sum that
// 

var TEH_NUM = "";

var i, sum = 0;

for(i = 0; i < TEH_NUM.length; i++) {
    sum += parseInt(TEH_NUM[i], 10);
}
